import NextAuth, { DefaultSession, type JWT } from "next-auth"

declare module "next-auth" {
	interface Session {
		user: {
			id: string
			role: "USER" | "ADMIN"
			disabled?: boolean
			emailVerified?: Date | null
		} & DefaultSession["user"]
	}

	interface User {
		id: string
		role: "USER" | "ADMIN"
		disabled?: boolean
		emailVerified?: Date | null
	}
}

declare module "next-auth/jwt" {
	interface JWT {
		id: string
		role: "USER" | "ADMIN"
		disabled?: boolean
		emailVerified?: Date | null
	}
}
